import React, { useEffect, useState } from 'react'
import Navbar from '../navbar/Navbar'
import MenuBurger from '../menuBurger/MenuBurger'
import url from '../../api/url'
import './DataArea.scss'


const DataArea = () => {
    const [mails, setMails] = useState()
    const [userId, setUserId] = useState()
    const [modelId, setModelId] = useState()
    const [token, setToken] = useState()
    const [choiceModel, setChoiceModel] = useState('Tout')
    const [choiceCategory, setChoiceCategory] = useState('')
    const [models, setModels] = useState([])
    const [selectModel, setSelectModel] = useState(false)

    useEffect(() => {
        if (localStorage.getItem('userId')) {
            setUserId(localStorage.getItem('userId'))
            setToken(localStorage.getItem('token'))
        } else {
            setUserId(sessionStorage.getItem('userId'))
            setToken(sessionStorage.getItem('token'))
        }
        if (sessionStorage.getItem('modelId')) {
            setModelId(sessionStorage.getItem('modelId'))
        }
    }, [])

    useEffect(() => {
        findAll()
    }, [userId, token, modelId])

    console.log(userId)

    const findAll = () => {
        fetch(`${url}/mail/findByUser/${userId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Acces-Control-Allow-Origin': { origin },
                'authorization': token
            }
        })
            .then(res => res.json())
            .then(res => console.log(res))
    }

    useEffect(() => {
        fetch(`${url}/model/findAll/${userId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Acces-Control-Allow-Origin': { origin },
                'authorization': token
            }
        })
            .then(res => res.json())
            .then(res => setModels(res))
    }, [userId, token])

    const modelSelectFunction = (name) => {
        console.log(name)
        setSelectModel(false)
        setChoiceModel(name)
    }

    console.log(models)

    return (
        <div className="containerModelArea">
            {window.innerWidth > 1280 ?
                <Navbar type={"data"} />
                :
                <MenuBurger type={"models"} />}
            <div className="contentDataArea">
                <div className="headSearchData">
                    <div className="choiceSearchData">
                        <p onClick={() => { setSelectModel() }} className="textChoiceDataGet">{choiceModel}</p>
                        {selectModel &&
                            <div className="listModelsData">
                                {models.length && models.map(model => {
                                    return (
                                        <p className="textChoiceData" onClick={() => {modelSelectFunction(model.name)}}>{model.name}</p>
                                    )
                                })}
                            </div>}

                    </div>
                    {choiceModel !== 'Tout' &&
                        <div className="choiceSearchData">
                            <p className="textChoiceData">{choiceModel}</p>
                        </div>}
                </div>
            </div>
        </div>
    )
}

export default DataArea
