import React, { useEffect, useState } from 'react'
import Navbar from '../navbar/Navbar'
import MenuBurger from '../menuBurger/MenuBurger'
import url from '../../api/url'
import './DataArea.scss'


const DataArea = () => {
    const [mails, setMails] = useState()
    const [userId, setUserId] = useState()
    const [modelId, setModelId] = useState()
    const [token, setToken] = useState()
    const [choiceModel, setChoiceModel] = useState('Tout')
    const [choiceCategory, setChoiceCategory] = useState('Tout')
    const [models, setModels] = useState([])
    const [selectModel, setSelectModel] = useState(false)
    const [categorys, setCategorys] = useState([])
    const [categorySelect, setCategorySelect] = useState()
    const [selectCategory, setSelectCategory] = useState(false)
    const [categoryId, setCategoryId] = useState()
    const [contacts, setContacts] = useState([])

    useEffect(() => {
        if (localStorage.getItem('userId')) {
            setUserId(localStorage.getItem('userId'))
            setToken(localStorage.getItem('token'))
        } else {
            setUserId(sessionStorage.getItem('userId'))
            setToken(sessionStorage.getItem('token'))
        }
    }, [])

    useEffect(() => {
        findAll()
    }, [userId, token, modelId])

    console.log(userId)

    const findAll = () => {
        fetch(`${url}/mail/findByUser/${userId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Acces-Control-Allow-Origin': { origin },
                'authorization': token
            }
        })
            .then(res => res.json())
            .then(res => console.log(res))
    }

    useEffect(() => {
        fetch(`${url}/model/findAll/${userId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Acces-Control-Allow-Origin': { origin },
                'authorization': token
            }
        })
            .then(res => res.json())
            .then(res => setModels(res))
    }, [userId, token])

    useEffect(() => {
        fetch(`${url}/category/findAll/${userId}/${modelId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Acces-Control-Allow-Origin': { origin },
                'authorization': token
            }
        })
            .then(res => res.json())
            .then(res => {
                setCategorys(res)
                if (!categorySelect && res[0]) setCategorySelect(res[0].id)
            })
    }, [userId, modelId])

    useEffect(() => {
        fetch(`${url}/mail/find/${userId}/${modelId}/${categoryId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Acces-Control-Allow-Origin': { origin },
                'authorization': token
            }
        })
            .then(res => res.json())
            .then(res => {
                setContacts(res)
            })
    }, [categoryId, userId, modelId])



    const modelSelectFunction = (model) => {
        console.log(model.id)
        setSelectModel(false)
        setChoiceModel(model.name)
        setModelId(model.id)
    }

    const categorySelectFunction = (category) => {
        setSelectCategory(false)
        setChoiceCategory(category.name)
        setCategoryId(category.id)
    }

    const updateColor = (color) => {
        fetch(`${url}/mail/update/${props.userId}/${props.modelId}/${props.categoryId}/${mailId}`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
              'Access-Control-Allow-Origin': `${origin}`,
              'authorization': props.token
            },
            body: JSON.stringify({
                color : color
            })
        })
    }


    console.log(categorys)

    return (
        <div className="containerModelArea">
            {window.innerWidth > 1280 ?
                <Navbar type={"data"} />
                :
                <MenuBurger type={"models"} />}
            <div className="headSearchData">
                <div className="choiceSearchData">
                    <p onClick={() => { setSelectModel(!selectModel) }} className="textChoiceDataGet">{choiceModel}</p>
                </div>
                {choiceModel !== 'Tout' &&
                    <div className="choiceSearchData">
                        <p onClick={() => { setSelectCategory(!selectCategory) }} className="textChoiceDataGet">{choiceCategory}</p>
                    </div>}
            </div>
            <div className="contentDataArea">
                {selectModel &&
                    <div className="listModelsData">
                        {models.length && models.map(model => {
                            return (
                                <p className="textChoiceData" onClick={() => { modelSelectFunction(model) }}>{model.name}</p>
                            )
                        })}
                    </div>}
                {selectCategory &&
                    <div className="listCategorysData">
                        {categorys.length && categorys.map(category => {
                            return (
                                <p className="textChoiceData" onClick={() => { categorySelectFunction(category) }}>{category.name}</p>
                            )
                        })}
                    </div>}
                {contacts.length && contacts.map(contact => {
                    return(
                        <div className="containerContact">
                            <p className="contentInfoContact">{contact.phone}</p>
                            <p className="contentInfoContact">{contact.email}</p>
                            <img onClick={() => {setChooseColor(!chooseColor)}} src={require('./image/color_icon.png')} className="colorIcon" alt="choose color"/>
                            {chooseColor &&
                            <div>
                                <div className="colorGree"/>
                                <div className="colorBlue"/>
                                <div className="colorOrange"/>
                                <div className="colorRouge"/>

                            </div>}
                        </div>
                    )
                })}
            </div>
        </div>
    )
}

export default DataArea
